const apiKey = process.env.binomPublic;
const url = 'https://pierdun.com/public/api/v1/info/affiliate_network';

const params = new URLSearchParams({"datePreset": "last_7_days", "timezone": "UTC", "limit": 5, "offset": 0});

fetch(`${url}?${params}`, {
  method: 'GET',
  headers: {
    'api-key': apiKey,
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
})
.then(response => response.json())
.then(data => console.log('Success:', data))
.catch(error => console.error('Error:', error));

